import React from 'react';

const About = ({ darkMode }) => {
  return (
    <section 
      id="about" 
      className={`section ${darkMode ? 'dark' : ''}`} 
      style={{ 
        background: 'linear-gradient(135deg, #0a0a0a 0%, #1a1a2e 20%, #16213e 40%, #0f3460 60%, #1a1a2e 80%, #0a0a0a 100%)',
        transition: 'all 0.3s ease',
        position: 'relative',
        overflow: 'hidden'
      }}
    >
      <div style={{
        position: 'absolute',
        top: 0,
        left: 0,
        right: 0,
        bottom: 0,
        background: `
          radial-gradient(circle at 20% 30%, rgba(116, 185, 255, 0.15) 0%, transparent 40%),
          radial-gradient(circle at 80% 70%, rgba(52, 152, 219, 0.1) 0%, transparent 40%),
          radial-gradient(circle at 40% 80%, rgba(0, 206, 201, 0.08) 0%, transparent 40%),
          linear-gradient(45deg, transparent 30%, rgba(116, 185, 255, 0.03) 50%, transparent 70%)
        `,
        zIndex: 1
      }} />

      {/* Animated particles */}
      <div style={{
        position: 'absolute',
        top: 0,
        left: 0,
        right: 0,
        bottom: 0,
        background: 'url("data:image/svg+xml,%3Csvg width="60" height="60" viewBox="0 0 60 60" xmlns="http://www.w3.org/2000/svg"%3E%3Cg fill="none" fill-rule="evenodd"%3E%3Cg fill="%2374b9ff" fill-opacity="0.1"%3E%3Ccircle cx="30" cy="30" r="1.5"/%3E%3C/g%3E%3C/g%3E%3C/svg%3E")',
        backgroundSize: '60px 60px',
        animation: 'float 20s linear infinite',
        zIndex: 1
      }} />
      <div className="container" style={{ position: 'relative', zIndex: 2 }}>
        <h2>About Me</h2>
        <div style={{
          display: 'grid',
          gridTemplateColumns: 'repeat(auto-fit, minmax(300px, 1fr))',
          gap: '40px',
          marginTop: '50px'
        }}>
          {/* Personal Info */}
          <div style={{
            background: 'rgba(45, 45, 45, 0.8)',
            padding: '30px',
            borderRadius: '20px',
            border: '1px solid rgba(116, 185, 255, 0.2)',
            animation: 'fadeInUp 0.8s ease-out 0.2s both'
          }}>
            <h3 style={{
              color: '#74b9ff',
              marginBottom: '20px',
              fontFamily: 'Poppins, sans-serif',
              fontSize: '1.5rem'
            }}><i className="fas fa-user"></i> Personal</h3>
            <p style={{
              fontSize: '1.1rem',
              lineHeight: '1.8',
              color: '#ccc'
            }}>
              I'm a passionate software developer with experience in building web applications 
              and systems. I enjoy creating efficient, scalable solutions and learning new technologies. 
              With a strong foundation in both frontend and backend development, I love turning 
              ideas into reality through clean, maintainable code.
            </p>
          </div>

          {/* Education */}
          <div style={{
            background: 'rgba(45, 45, 45, 0.8)',
            padding: '30px',
            borderRadius: '20px',
            border: '1px solid rgba(116, 185, 255, 0.2)',
            animation: 'fadeInUp 0.8s ease-out 0.4s both'
          }}>
            <h3 style={{
              color: '#74b9ff',
              marginBottom: '20px',
              fontFamily: 'Poppins, sans-serif',
              fontSize: '1.5rem'
            }}><i className="fas fa-graduation-cap"></i> Education</h3>
            <div style={{ marginBottom: '25px' }}>
              <h4 style={{
                color: '#fff',
                fontSize: '1.2rem',
                marginBottom: '8px'
              }}>Bachelor of Computer Science</h4>
              <p style={{
                color: '#74b9ff',
                fontSize: '1rem',
                marginBottom: '5px'
              }}>University of Technology</p>
              <p style={{
                color: '#aaa',
                fontSize: '0.9rem'
              }}>2020 - 2024</p>
            </div>
            <div>
              <h4 style={{
                color: '#fff',
                fontSize: '1.2rem',
                marginBottom: '8px'
              }}>High School Diploma</h4>
              <p style={{
                color: '#74b9ff',
                fontSize: '1rem',
                marginBottom: '5px'
              }}>Al-Mustaqbal High School</p>
              <p style={{
                color: '#aaa',
                fontSize: '0.9rem'
              }}>2016 - 2020</p>
            </div>
          </div>
        </div>
      </div>
    </section>
  );
};

export default About;
