import React, { useEffect } from 'react';
import Header from './components/Header';
import About from './components/About';
import Skills from './components/Skills';
import Projects from './components/Projects';
import Contact from './components/Contact';
import Footer from './components/Footer';
import './App.css';

function App() {
  useEffect(() => {
    // Scroll progress bar
    const updateScrollProgress = () => {
      const scrollTop = window.pageYOffset;
      const docHeight = document.body.scrollHeight - window.innerHeight;
      const scrollPercent = (scrollTop / docHeight) * 100;

      let progressBar = document.querySelector('.scroll-progress');
      if (!progressBar) {
        progressBar = document.createElement('div');
        progressBar.className = 'scroll-progress';
        document.body.appendChild(progressBar);
      }
      progressBar.style.width = scrollPercent + '%';
    };

    // Parallax effect for header
    const handleParallax = () => {
      const scrolled = window.pageYOffset;
      const header = document.querySelector('header');
      if (header) {
        // Move header up faster and add scale effect
        header.style.transform = `translateY(${scrolled * 0.7}px) scale(${Math.max(0.8, 1 - scrolled / 2000)})`;
        header.style.opacity = Math.max(0, 1 - scrolled / 800);
        header.style.filter = `blur(${Math.min(scrolled / 200, 10)}px)`;
      }

      // Animate moon and stars
      const moon = header?.querySelector('div[style*="120px"]');
      if (moon) {
        moon.style.transform = `translateY(${scrolled * -0.3}px) rotate(${scrolled * 0.1}deg)`;
      }

      // Parallax for content sections
      const sections = document.querySelectorAll('.section');
      sections.forEach((section, index) => {
        const rect = section.getBoundingClientRect();
        const scrollPercent = (window.innerHeight - rect.top) / window.innerHeight;

        if (scrollPercent > 0 && scrollPercent < 1) {
          section.style.transform = `translateY(${(1 - scrollPercent) * 50}px)`;
          section.style.opacity = Math.min(1, scrollPercent + 0.3);
        } else if (scrollPercent >= 1) {
          section.style.transform = 'translateY(0)';
          section.style.opacity = '1';
        }
      });
    };

    const handleScroll = () => {
      updateScrollProgress();
      handleParallax();
    };

    window.addEventListener('scroll', handleScroll);
    return () => window.removeEventListener('scroll', handleScroll);
  }, []);

  return (
    <div className="App dark">
      <Header darkMode={true} />
      <About darkMode={true} />
      <Skills darkMode={true} />
      <Projects darkMode={true} />
      <Contact darkMode={true} />
      <Footer darkMode={true} />
    </div>
  );
}

export default App;
