import React from 'react';

const Skills = ({ darkMode }) => {
  const skills = [
    'React', 'JavaScript', 'Java', 'Python',
    'Node.js', 'PostgreSQL', 'Git', 'HTML/CSS'
  ];

  return (
    <section 
      id="skills" 
      className={`section ${darkMode ? 'dark' : ''}`}
      style={{
        background: darkMode ? '#1a1a1a' : 'white',
        transition: 'all 0.3s ease'
      }}
    >
      <div className="container">
        <h2>Skills</h2>
        <div style={{
          display: 'grid',
          gridTemplateColumns: 'repeat(auto-fit, minmax(150px, 1fr))',
          gap: '20px',
          marginTop: '40px'
        }}>
          {skills.map((skill, index) => (
            <div 
              key={index} 
              style={{
                padding: '20px',
                background: darkMode 
                  ? 'linear-gradient(135deg, #74b9ff 0%, #0984e3 100%)'
                  : 'linear-gradient(135deg, #3498db 0%, #2980b9 100%)',
                color: 'white',
                borderRadius: '12px',
                fontWeight: 'bold',
                cursor: 'pointer',
                transition: 'all 0.3s ease',
                animation: `fadeInUp 0.6s ease-out ${index * 0.1}s both`,
                boxShadow: darkMode 
                  ? '0 4px 15px rgba(116, 185, 255, 0.2)'
                  : '0 4px 15px rgba(52, 152, 219, 0.2)'
              }}
              onMouseEnter={(e) => {
                e.target.style.transform = 'translateY(-5px) scale(1.05)';
                e.target.style.boxShadow = darkMode 
                  ? '0 8px 25px rgba(116, 185, 255, 0.4)'
                  : '0 8px 25px rgba(52, 152, 219, 0.4)';
              }}
              onMouseLeave={(e) => {
                e.target.style.transform = 'translateY(0) scale(1)';
                e.target.style.boxShadow = darkMode 
                  ? '0 4px 15px rgba(116, 185, 255, 0.2)'
                  : '0 4px 15px rgba(52, 152, 219, 0.2)';
              }}
            >
              {skill}
            </div>
          ))}
        </div>
      </div>
    </section>
  );
};

export default Skills;
